import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { Grid } from '@mui/material';
import { getDetails } from '../Api/api';




export const BasicCard = ({match}) => {

    const handleClick = (id) => {

        getDetails(id).then((data) => console.log(data)).catch((error) => alert("Could not load data"))
    }
    

    return (
        <Card sx={{ minWidth: 275 }} variant="outlined" style={{ margin: 5}}>
            <CardContent>
                <Grid container justifyContent='space-evenly' alignItems="center" spacing={4}>
                    <Grid item>
                       <img style={{ width: 45 }} src={ (match.teamInfo[0]) !== undefined ?(match.teamInfo[0].img) : ("")}/>
                        <Typography variant="h5">{match.teams[0]}</Typography>

                    </Grid>
                    <Grid item >

                        <img style={{ width: 85 }} src={require("../img/vs.png")} />
                    </Grid>
                    <Grid item>
                    <img style={{ width: 45 }} src={ (match.teamInfo[1]) !== undefined ?(match.teamInfo[1].img) : ("")}/>
                        <Typography variant="h5">{match.teams[1]}</Typography>
                    </Grid>
                </Grid>
            </CardContent>
            <CardActions>

                <Grid container justifyContent='space-evenly'  >
                    <Button item variant="contained" color="primary" onClick= {
                        () => {
                                handleClick(match.id)
                        }
                    }>Show Details</Button>
                    <Button item variant="contained" color="primary">Start Time {match.dateTimeGMT}</Button>
                    {console.log(match)}
                </Grid>

            </CardActions>
        </Card>
    );
}
